mapper.select:
select usr.uuid, usr.username, usr.first_name, usr.last_name, usr.title from (select * from auth.users u where u.row_status = 'ACTIVE' and u.uuid in
#{targetUserId, typeHandler=az.rock.flyjob.auth.dataAccess.mapper.type.handler.UUIDTypeHandler}
) usr where not exists( select * from network.block_relation br where br.row_status = 'ACTIVE' and ((br.user_id = #{authId} and br.target_user_id = usr.uuid) OR (br.target_user_id = #{authId} and br.user_id = usr.uuid)) );
